import React, { useState } from 'react';
import './AmenitiesPopup.css';

const AmenitiesPopup = ({ images }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const prevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const nextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="popup-container">
      <div className="popup">
        <div className="popup-content">
          <img src={images[currentImageIndex].src} alt={images[currentImageIndex].alt} className="popup-image" />
          <div className="popup-text">
            <h2>{images[currentImageIndex].title}</h2>
            <p>{images[currentImageIndex].description}</p>
          </div>
        </div>
        <div className="popup-navigation">
          <button className="popup-arrow" onClick={prevImage}>
            &lt;
          </button>
          <button className="popup-arrow" onClick={nextImage}>
            &gt;
          </button>
        </div>
      </div>
    </div>
  );
};

export default AmenitiesPopup;
