import React, { useState } from 'react';
import './AmenitiesPopup.css';
import image1 from './island.jpeg';
import image2 from './island5.jpeg';
import image3 from './island6.jpeg';
import image4 from './island8.jpeg';

const AmenitiesPopup = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [image1, image2, image3, image4];
  const texts = ["Text for Image 1", "Text for Image 2", "Text for Image 3", "Text for Image 4"];

  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const onClose = () => {
    setShowPopup(false);
  };

  const handleKeyDown = (event) => {
    if (event.key === "Escape") {
      onClose();
    }
  };

  useEffect(() => {
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  return (
    <div className="popup-container">
      <div className="popup">
      <button className="close-button" onClick={onClose}>
          &times;
        </button>    
      <div className="arrow-container">
        <button className="arrow previous-arrow" onClick={prevImage}>
        &#8593;
        </button>
        </div>
        <div className="popup-content">
          <img src={images[currentIndex]} alt={`Image ${currentIndex + 1}`} />
          <div className="text-container">
            <h2>Image {currentIndex + 1}</h2>
            <p>{texts[currentIndex]}</p>
          </div>
        </div>
        <div className="arrow-container">
        <button className="arrow next-arrow" onClick={nextImage}>
        &#8595;
        </button>
        </div>
      </div>
    </div>
  );
};

export default AmenitiesPopup;
