import React, { useState, useEffect } from 'react';
import './Location.css';
import Header from './Header';
import Footer from './Footer';
import Advertisement from './Advertisement';
import image1 from './island.jpeg';
import image2 from './island5.jpeg';
import image3 from './island6.jpeg';
import image4 from './island8.jpeg';
import image5 from './Helicopter.jpeg';

const Location = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    { src: image1, alt: 'Image 1' },
    { src: image2, alt: 'Image 2' },
    { src: image3, alt: 'Image 3' },
    { src: image4, alt: 'Image 4' },
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === images.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000);

    return () => {
      clearInterval(interval);
    };
  }, []);

  return (
    <div>
      <Header />
      <main>
        <Advertisement />
        <br />
        <div className="main-section">
          <div className="image-container">
            <img
              src={images[currentImageIndex].src}
              alt={images[currentImageIndex].alt}
              className="image"
            />
          </div>
          <div className="text-container">
            <h2>Whispers of Love Peninsula</h2>
            <p>
              Congratulations on finding the perfect destination for your
              wedding! At Whispers of Love Peninsula, we believe that every
              love story deserves a breathtaking backdrop, and our island
              provides just that.
            </p>
          </div>
        </div>
        <div className="banner-container">
                <div className="banner">
                <p>HOW TO GET HERE?</p>
                </div>
            </div>
            <div className="main-section">
          <div className="text-container">
            <h2>AeroLux Alliance</h2>
            <p>
            Experience the ultimate luxury and thrill on your special day with AeroLux Alliance, the perfect wedding chauffeur for your dream destination wedding on a stunning island. Soar through the skies in elegance and style, creating an unforgettable entrance that will leave your guests in awe. Velvet Thunder offers unparalleled comfort and convenience, ensuring a seamless and magical journey as you embark on a new chapter of your life. Let this exquisite helicopter transport you to breathtaking vistas, capturing the essence of romance and adventure. Trust Velvet Thunder to elevate your wedding experience to new heights, creating memories that will last a lifetime.
            </p>
          </div>
          <div className="image-container">
            <img
              src={image5}
              alt={'Image 5'}
              className="image"
            />
          </div>
        </div>    
        <div className="column">
            <h2>BOOK ONLY HELICOPTER</h2>
            <button className="fancy-button">BOOK NOW FOR $100</button>
          </div>
          <div className="column">
            <h2>BOOK BOTH</h2>
            <button className="fancy-button">BOOK NOW FOR $100</button>
          </div>
      </main>
      <Footer />
    </div>
  );
};

export default Location;
