import React, { useState } from 'react';
import './AmenitiesPopup.css';
import image1 from './island.jpg';
import image2 from './island5.jpg';
import image3 from './island6.jpg';
import image4 from './island8.jpg';

const AmenitiesPopup = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [image1, image2, image3, image4];
  const texts = ["Text for Image 1", "Text for Image 2", "Text for Image 3", "Text for Image 4"];

  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  return (
    <div className="popup-container">
      <div className="popup">
        <button className="arrow previous-arrow" onClick={prevImage}>
          &lt;
        </button>
        <div className="popup-content">
          <img src={images[currentIndex]} alt={`Image ${currentIndex + 1}`} />
          <div className="text-container">
            <h2>Image {currentIndex + 1}</h2>
            <p>{texts[currentIndex]}</p>
          </div>
        </div>
        <button className="arrow next-arrow" onClick={nextImage}>
          &gt;
        </button>
      </div>
    </div>
  );
};

export default AmenitiesPopup;
