import React, { useState } from 'react';
import './Location.css';
import Header from './Header';
import Footer from './Footer';
import Advertisement from './Advertisement';

const Location = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const images = [
    { src: require('./island.jpeg').default, alt: 'Image 1' },
    { src: 'island.jpeg', alt: 'Image 2' },
    { src: 'island.jpeg', alt: 'Image 3' },
  ];

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === 0 ? images.length - 1 : prevIndex - 1
    );
  };

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) =>
      prevIndex === images.length - 1 ? 0 : prevIndex + 1
    );
  };

  return (
    <div>
      <Header />
      <main>
        <Advertisement />
        <br></br>
        <div className="main-section">
          <div className="image-container">
            <img
              src={images[currentImageIndex].src}
              alt={images[currentImageIndex].alt}
              className="image"
            />
            <div className="arrow-container">
              <button onClick={handlePrevImage} className="arrow left">
                &lt;
              </button>
              <button onClick={handleNextImage} className="arrow right">
                &gt;
              </button>
            </div>
          </div>
          <div className="text-container">
            <h2>Whispers of Love Peninsula</h2>
            <p>
              Congratulations on finding the perfect destination for your
              wedding! At Whispers of Love Peninsula, we believe that every
              love story deserves a breathtaking backdrop, and our island
              provides just that.
            </p>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Location;
